package com.emulators.cubicCMS.constants;

public interface SQLConstants extends DBConstants{

	//SQL Insert Query's
	String CUSTOMER_INSERT_QUERY = "INSERT INTO cms.customer ("+CUSTOMER_REFERENCE_NUMBER+", "+CUSTOMER_TITLE+", "+CUSTOMER_FIRST_NAME+", "+CUSTOMER_MIDDLE_NAME+", "+CUSTOMER_SURNAME+", "+CUSTOMER_TELEPHONE_NUMBER+", "+CUSTOMER_EMAIL+", "+CUSTOMER_DOB+", "+CUSTOMER_GENDER+", "+CUSTOMER_ADDRESS_LINE1+", "+CUSTOMER_ADDRESS_LINE2+", "+CUSTOMER_ADDRESS_LINE3+", "+CUSTOMER_ADDRESS_LINE4+", "+CUSTOMER_ADDRESS_LINE5+", "+CUSTOMER_ADDRESS_LINE6+", "+CUSTOMER_POST_CODE+") VALUES ("+PARAM_CUSTOMER_REFERENCE_NUMBER+", "+PARAM_CUSTOMER_TITLE+", "+PARAM_CUSTOMER_FIRST_NAME+", "+PARAM_CUSTOMER_MIDDLE_NAME+", "+PARAM_CUSTOMER_SURNAME+", "+PARAM_CUSTOMER_TELEPHONE_NUMBER+", "+PARAM_CUSTOMER_EMAIL+", "+PARAM_CUSTOMER_DOB+", "+PARAM_CUSTOMER_GENDER+", "+PARAM_CUSTOMER_ADDRESS_LINE1+", "+PARAM_CUSTOMER_ADDRESS_LINE2+", "+PARAM_CUSTOMER_ADDRESS_LINE3+", "+PARAM_CUSTOMER_ADDRESS_LINE4+", "+PARAM_CUSTOMER_ADDRESS_LINE5+", "+PARAM_CUSTOMER_ADDRESS_LINE6+", "+PARAM_CUSTOMER_POST_CODE+");";
	String CARD_INSERT_QUERY = "INSERT INTO cms.card ("+CARD_CUSTOMER_REFERENCE_NUMBER+", "+CARD_DESIGNATION+", "+CARD_NICK_NAME+", "+CARD_IDENTIFIER+", "+CARD_STATE+", "+CARD_EXPIRY+", "+CARD_PHOTO+") VALUES ((select "+ CARD_CUSTOMER_REFERENCE_NUMBER +" from cms.customer where "+ CUSTOMER_REFERENCE_NUMBER +"='"+PARAM_CARD_CUSTOMER_REFERENCE_NUMBER+"'), "+PARAM_CARD_DESIGNATION+", "+PARAM_CARD_NICK_NAME+", "+PARAM_CARD_IDENTIFIER+", "+PARAM_CARD_STATE+", "+PARAM_CARD_EXPIRY+", "+PARAM_CARD_PHOTO+");";
	String PRODUCT_INSERT_QUERY = "INSERT INTO cms.product("+PRODUCT_CARD_ID+", "+PRODUCT_NAME+", "+PRODUCT_PRICE+", "+PRODUCT_PAYMENT_REFERENCE+", "+PRODUCT_START_DATE+", "+PRODUCT_EXPIRY_DATE+") VALUES ((select "+PRODUCT_CARD_ID+" from cms.card where "+CARD_IDENTIFIER+"='"+PARAM_PRODUCT_CARD_ID+"'), "+PARAM_PRODUCT_NAME+", "+PARAM_PRODUCT_PRICE+", "+PARAM_PRODUCT_PAYMENT_REFERENCE+", "+PARAM_PRODUCT_START_DATE+", "+PARAM_PRODUCT_EXPIRY_DATE+");";	
}
